int fputc(int ch, FILE *f)
{
  uint8_t c = (uint8_t)ch;
  HAL_UART_Transmit(&huart2, &c, 1, HAL_MAX_DELAY);
  return ch;
}


void Printf_Array(uint8_t *str, uint8_t *Array,uint16_t Len)
{
    printf("%s : ", str);

    for(int i = 0; i < Len; i++)
        printf("0x%02x, ", Array[i]);

    printf("\n");
}



